import React, { ReactElement } from "react";
import { PageIds } from "../../stores/Interfaces";

import "./styles.scss";

const Header = (): ReactElement => {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const [isStepMenu, setIsStepMenu] = React.useState<boolean>(true);
  const handleClick = (
    event: React.MouseEvent<HTMLLIElement>,
    isStepwise: boolean
  ) => {
    setIsStepMenu(isStepwise);
    setAnchorEl((event as any).target);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <header className="app-header">
      <h1>TRAIT</h1>
      <nav>
        <ul>
          {Object.keys(PageIds).map((key) => (
            <li className="option">
              <a href="#about-me">RESET</a>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};
export default Header;
