import { Grid, Avatar } from "@material-ui/core";
import React, { ReactElement } from "react";
import { Organizer } from "../../stores/Interfaces";

import "./styles.scss";

const OrganizerEntry = ({
  organizer,
}: {
  organizer: Organizer;
}): ReactElement => {
  return (
    <div>
      <Avatar
        alt={organizer.name}
        src={`${process.env.PUBLIC_URL}/images/${organizer.img}`}
      />
      <div>
        <div className="name">{organizer.name}</div>
        <div className="name">{organizer.affliation}</div>
      </div>
    </div>
  );
};

const OrganizerList = ({
  organizers,
}: {
  organizers: Organizer[];
}): ReactElement => {
  return (
    <Grid container spacing={3}>
      {organizers.map((organizer: Organizer) => (
        <Grid item xs={12} sm={6} key={organizer.name}>
          <span>{organizer.description}</span>
        </Grid>
      ))}
    </Grid>
  );
};
export default OrganizerList;
