import { Grid } from "@material-ui/core";
import React, { ReactElement } from "react";
import { CFP } from "../../stores/Interfaces";

import "./styles.scss";

const CFPText = ({ cfp }: { cfp: CFP }): ReactElement => {
  return (
    <div className="cfp-text">
      <div className="info-block-wrapper">
        <div className="info-block">
          <div className="info-title">Important Dates</div>
          <p>
            {cfp.dates.map((date, i) => (
              <div key={i}>
                <div className="row">
                  <div className="row-key">{date.type}</div>
                  <div className="row-value">{date.date}</div>
                </div>
                {date.description && (
                  <div className="description">{date.description}</div>
                )}
              </div>
            ))}
          </p>
        </div>
        <div className="info-block">
          <div className="info-title">Submssion format</div>
          <p>
            <Grid
              container
              spacing={3}
              id={PageIds.Organizers}
              className="organizer-list"
            >
              <Grid item lg={3} md={3} sm={3} xs={12} key={organizer.name}>
                <div className="row">
                  <div className="row-key">Platform</div>
                  <a className="row-value" href={cfp.submit.platform.url}>
                    {cfp.submit.platform.name}
                  </a>
                </div>
                <div className="row">
                  <div className="row-value">Template</div>
                  <a className="date" href={cfp.submit.template.url}>
                    {cfp.submit.template.name}
                  </a>
                </div>
                <div className="date">
                  <div className="type">Format</div>
                  <div>{cfp.submit.format}</div>
                </div>{" "}
              </Grid>
            </Grid>
          </p>
        </div>
      </div>

      <div className="info-title">Description</div>
      <p>{cfp.description}</p>
      <p>Themes include, but are not limited to:</p>
      <ul>
        {cfp.themes.map((theme, i) => (
          <li key={i}>{theme}</li>
        ))}
      </ul>
    </div>
  );
};
export default CFPText;
